ValidationRule.errorConditionFormula:
AND(
  OR(
    ISPICKVAL(PRIORVALUE(Status), 'Completed'),
    ISPICKVAL(PRIORVALUE(Status), 'Cancelled')
  ),
  NOT(
    OR(
      $Permission.Integration_User,
      $Profile.Name = 'System Administrator'
    )
  ),
  TEXT(Salesforce_Order_Type__c) <> 'ZOR'
)
