customDetailFormulas.calculatedFormula:
IF(
 /* 1. Prüfen, ob ALLE Felder leer sind */
 AND(
   ISBLANK(LAST_ACTIVITY),
   ISBLANK(Opportunity.Last_Chatter_Post_Date__c),
   ISBLANK(LAST_UPDATE),
   ISBLANK(LAST_STAGE_CHANGE_DATE)
 ),
 NULL, /* Wenn ja, gib NULL zurück */
 /* 2. Prüfen, ob LAST_ACTIVITY das Maximum ist */
 IF(
   AND(
     NOT(ISBLANK(LAST_ACTIVITY)),
     OR(ISBLANK(Opportunity.Last_Chatter_Post_Date__c), LAST_ACTIVITY >= DATEVALUE(Opportunity.Last_Chatter_Post_Date__c)),
     OR(ISBLANK(LAST_UPDATE), LAST_ACTIVITY >= DATEVALUE(LAST_UPDATE)),
     OR(ISBLANK(LAST_STAGE_CHANGE_DATE), LAST_ACTIVITY >= DATEVALUE(LAST_STAGE_CHANGE_DATE))
   ),
   LAST_ACTIVITY, /* Wenn ja, gib LAST_ACTIVITY zurück */
   /* 3. Prüfen, ob Chatter Post das Maximum ist */
   IF(
     AND(
       NOT(ISBLANK(Opportunity.Last_Chatter_Post_Date__c)),
       OR(ISBLANK(LAST_UPDATE), DATEVALUE(Opportunity.Last_Chatter_Post_Date__c) >= DATEVALUE(LAST_UPDATE)),
       OR(ISBLANK(LAST_STAGE_CHANGE_DATE), DATEVALUE(Opportunity.Last_Chatter_Post_Date__c) >= DATEVALUE(LAST_STAGE_CHANGE_DATE))
     ),
     DATEVALUE(Opportunity.Last_Chatter_Post_Date__c), /* Wenn ja, gib Chatter Post zurück */
     /* 4. Prüfen, ob Last Modified das Maximum ist */
     IF(
       AND(
         NOT(ISBLANK(LAST_UPDATE)),
         OR(ISBLANK(LAST_STAGE_CHANGE_DATE), DATEVALUE(LAST_UPDATE) >= DATEVALUE(LAST_STAGE_CHANGE_DATE))
       ),
       DATEVALUE(LAST_UPDATE), /* Wenn ja, gib Last Modified zurück */
       /* 5. Ansonsten MUSS es Last Stage Change sein (oder NULL, was durch DATEVALUE abgefangen wird) */
       DATEVALUE(LAST_STAGE_CHANGE_DATE)
     )
   )
 )
)
